\begin{tikzpicture}[thin border nodes]
  \draw[help lines] (-3,-3) grid (4, 4);
  \draw[->] (-3,0) -- (4,0);
  \draw[->] (0,-3) -- (0,4);
  \draw[vector, seq1] (0,0) to["$v_1$"] (1,2);
  \draw[vector, seq2] (0,0) to["\strut$v_2$"'] (1,0);
  \point at (0,0);
  \point[seq3] at (2,2);
  \point[seq4] at (0,2);
  \point[seq5] at (2,4);
  \point[seq8] at (2,0);
  \point[seq7] at (-1,-2);

  \node[anchor=west] at (5, .75)
    {
      \begin{minipage}{0.6\linewidth}
         \begin{itemize}
         \color{seq3}
         \item $v_1+v_2$
         \color{seq4}
         \item $v_1-v_2$
         \color{seq5}
         \item $2v_1\color{gray}+0v_2$
         \color{seq8}
         \item $2v_2$
         \color{seq7}
         \item $-v_1$
         \end{itemize}
      \end{minipage}
    };
\end{tikzpicture}


